document.addEventListener("DOMContentLoaded",(()=>{document.querySelectorAll(".open-inline-search-btn").forEach((e=>{e.addEventListener("click",(n=>{document.querySelectorAll(".inline-search-holder").forEach((n=>{let l=n.querySelector(".inline-search"),t=l.querySelector(".search-pattern");if(!t)return;l.classList.remove("hidden"),e.style.display="none",t.focus();let r=l.querySelector(".close-inline-search-btn");r&&r.addEventListener("click",(()=>{l.classList.add("hidden"),e.style.display="inline-block",t.value=""}))}))}))}))}));